import { log } from "console";



const breakpoints = () => {

    let screen = window.screen;

    console.log(screen);

    if (screen.width >=800){

        console.log("LESS THAN 500");
    }
    else{
        console.log("GREATER THAN 500");
    }
}

export default breakpoints;
